import React, { useState, useEffect, useCallback } from 'react';
import ServiceCard from './ServiceCard';
import './Services1.css';

const Services1 = ({ wishlistItems, onAddToCart, onToggleWishlist, onViewProduct, cartItems, navigateTo, searchQuery, selectedVendor }) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [currentSlide, setCurrentSlide] = useState(0);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [vendorName, setVendorName] = useState('');

    // ✅ FILTER STATES
    const [showFilters, setShowFilters] = useState(false);
    const [filters, setFilters] = useState({
        priceRange: { min: '', max: '' },
        store: '',
        sortBy: ''
    });

    const slideImages = [
        "/IMAGES/service-hero-1.jpg",
        "/IMAGES/service-hero-2.jpg",
        "/IMAGES/service-hero-3.jpg"
    ];

    useEffect(() => {
        const slideInterval = setInterval(() => {
            setCurrentSlide(prev => (prev + 1) % slideImages.length);
        }, 5000);
        return () => clearInterval(slideInterval);
    }, [slideImages.length]);

    const getDistanceFromLatLonInKm = useCallback((lat1, lon1, lat2, lon2) => {
        const R = 6371;
        const deg2rad = (deg) => deg * (Math.PI / 180);
        const dLat = deg2rad(lat2 - lat1);
        const dLon = deg2rad(lon2 - lon1);
        const a =
            Math.sin(dLat / 2) * Math.sin(dLat / 2) +
            Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) *
            Math.sin(dLon / 2) * Math.sin(dLon / 2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
        return R * c;
    }, []);

    // ✅ COMBINED FETCH AND GEOLOCATION
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                setLoading(true);
                const response = await fetch('http://localhost:5000/api/products/category/Services');
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const data = await response.json();

                // Set vendor name if selected
                if (selectedVendor) {
                    setVendorName(selectedVendor);
                }

                // ✅ ADD DISTANCE IMMEDIATELY AFTER FETCH
                if (navigator.geolocation) {
                    navigator.geolocation.getCurrentPosition(
                        (position) => {
                            const userLat = position.coords.latitude;
                            const userLon = position.coords.longitude;

                            const productsWithDistance = data.map(product => {
                                if (product.lat && product.lon) {
                                    const distance = getDistanceFromLatLonInKm(userLat, userLon, product.lat, product.lon);
                                    return { 
                                        ...product, 
                                        distance: `${distance.toFixed(1)} km away`,
                                        distanceValue: parseFloat(distance.toFixed(1))
                                    };
                                }
                                return { ...product, distance: 'Distance N/A', distanceValue: 999 };
                            });
                            setProducts(productsWithDistance);
                            setFilteredProducts(productsWithDistance); // ✅ SET BOTH
                        },
                        (error) => {
                            console.warn(`Geolocation error: ${error.message}`);
                            const productsWithDefaultDistance = data.map(p => ({
                                ...p, 
                                distance: 'Within 5km',
                                distanceValue: 5
                            }));
                            setProducts(productsWithDefaultDistance);
                            setFilteredProducts(productsWithDefaultDistance); // ✅ SET BOTH
                        }
                    );
                } else {
                    console.warn("Geolocation is not supported by this browser.");
                    const productsWithDefaultDistance = data.map(p => ({
                        ...p, 
                        distance: 'Within 5km',
                        distanceValue: 5
                    }));
                    setProducts(productsWithDefaultDistance);
                    setFilteredProducts(productsWithDefaultDistance); // ✅ SET BOTH
                }

                setError(null);
            } catch (err) {
                console.error('Error fetching products:', err);
                setError('Failed to load products. Please try again later.');
                setProducts([]);
                setFilteredProducts([]); // ✅ CLEAR ON ERROR
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, [selectedVendor, getDistanceFromLatLonInKm]);

    // ✅ APPLY FILTERS
    useEffect(() => {
        if (products.length === 0) {
            return;
        }

        let filtered = [...products];

        // ✅ FILTER BY VENDOR
        if (selectedVendor) {
            filtered = filtered.filter(product => product.vendor === selectedVendor);
        }

        // ✅ FILTER BY SEARCH
        if (searchQuery && searchQuery.trim()) {
            const query = searchQuery.toLowerCase().trim();
            filtered = filtered.filter(product =>
                product.name?.toLowerCase().includes(query) ||
                product.vendor?.toLowerCase().includes(query) ||
                product.type?.toLowerCase().includes(query)
            );
        }

        // ✅ FILTER BY PRICE RANGE
        if (filters.priceRange.min) {
            filtered = filtered.filter(p => p.price >= parseFloat(filters.priceRange.min));
        }
        if (filters.priceRange.max) {
            filtered = filtered.filter(p => p.price <= parseFloat(filters.priceRange.max));
        }

        // ✅ FILTER BY STORE
        if (filters.store) {
            filtered = filtered.filter(p => 
                p.vendor?.toLowerCase().includes(filters.store.toLowerCase())
            );
        }

        // ✅ SORT BY
        if (filters.sortBy) {
            switch (filters.sortBy) {
                case 'price-low':
                    filtered.sort((a, b) => a.price - b.price);
                    break;
                case 'price-high':
                    filtered.sort((a, b) => b.price - a.price);
                    break;
                case 'distance':
                    filtered.sort((a, b) => (a.distanceValue || 999) - (b.distanceValue || 999));
                    break;
                case 'name-az':
                    filtered.sort((a, b) => a.name.localeCompare(b.name));
                    break;
                case 'name-za':
                    filtered.sort((a, b) => b.name.localeCompare(a.name));
                    break;
                default:
                    break;
            }
        }

        setFilteredProducts(filtered);
    }, [searchQuery, products, selectedVendor, filters]);

    // ✅ HANDLE FILTER CHANGE
    const handleFilterChange = (filterType, value) => {
        setFilters(prev => ({
            ...prev,
            [filterType]: value
        }));
    };

    // ✅ HANDLE PRICE RANGE CHANGE
    const handlePriceRangeChange = (type, value) => {
        setFilters(prev => ({
            ...prev,
            priceRange: {
                ...prev.priceRange,
                [type]: value
            }
        }));
    };

    // ✅ CLEAR ALL FILTERS
    const clearAllFilters = () => {
        setFilters({
            priceRange: { min: '', max: '' },
            store: '',
            sortBy: ''
        });
    };

    // ✅ COUNT ACTIVE FILTERS
    const getActiveFilterCount = () => {
        let count = 0;
        if (filters.priceRange.min || filters.priceRange.max) count++;
        if (filters.store) count++;
        if (filters.sortBy) count++;
        return count;
    };

    const isProductInWishlist = (productId) => {
        return wishlistItems.some(item => (item._id || item.id) === productId);
    };

    const handleBackToVendors = () => {
        navigateTo('services');
    };

    return (
        <>
            <section className="hero" id="hero">
                <div className="hero-carousel">
                    <div className="carousel-slides" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
                        {slideImages.map((img, index) => (
                            <div className="carousel-slide" key={index}>
                                <img src={img} alt={`Service slide ${index + 1}`} />
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            <section id="services" className="section">
                <div className="container">
                    <div className="vendor-header">
                        {selectedVendor ? (
                            <>
                                <button className="back-button" onClick={handleBackToVendors}>
                                    ← Back to Vendors
                                </button>
                                <h3 className="vendor-title">{vendorName}</h3>
                            </>
                        ) : (
                            <button className="back-button" onClick={() => navigateTo('general-services')}>
                                ← Back to Categories
                            </button>
                        )}
                    </div>
                    <div className="section-title">
                        <h2>{selectedVendor ? 'Products' : 'Services'}</h2>
                    </div>

                    {/* ✅ FILTER SECTION */}
                    {!loading && !error && (
                        <div className="filter-section">
                            <button 
                                className="filter-toggle-btn" 
                                onClick={() => setShowFilters(!showFilters)}
                            >
                                <i className="fas fa-filter"></i>
                                Filters {getActiveFilterCount() > 0 && `(${getActiveFilterCount()})`}
                            </button>
                            {getActiveFilterCount() > 0 && (
                                <button className="clear-filters-btn" onClick={clearAllFilters}>
                                    <i className="fas fa-times"></i> Clear All
                                </button>
                            )}
                            {searchQuery && (
                                <div className="search-results-info">
                                    {filteredProducts.length} result{filteredProducts.length !== 1 ? 's' : ''} found 
                                    {selectedVendor ? ` in ${selectedVendor}` : ''} for "{searchQuery}"
                                </div>
                            )}
                        </div>
                    )}

                    {/* ✅ FILTER PANEL */}
                    {showFilters && (
                        <div className="filter-panel">
                            <div className="filter-grid">
                                {/* Price Range */}
                                <div className="filter-group">
                                    <label><i className="fas fa-rupee-sign"></i> Price Range</label>
                                    <div className="price-range-inputs">
                                        <input
                                            type="number"
                                            placeholder="Min"
                                            value={filters.priceRange.min}
                                            onChange={(e) => handlePriceRangeChange('min', e.target.value)}
                                        />
                                        <span>to</span>
                                        <input
                                            type="number"
                                            placeholder="Max"
                                            value={filters.priceRange.max}
                                            onChange={(e) => handlePriceRangeChange('max', e.target.value)}
                                        />
                                    </div>
                                </div>

                                {/* Store/Vendor */}
                                <div className="filter-group">
                                    <label><i className="fas fa-store"></i> Store</label>
                                    <input
                                        type="text"
                                        placeholder="Search store"
                                        value={filters.store}
                                        onChange={(e) => handleFilterChange('store', e.target.value)}
                                    />
                                </div>

                                {/* Sort By */}
                                <div className="filter-group">
                                    <label><i className="fas fa-sort"></i> Sort By</label>
                                    <select 
                                        value={filters.sortBy} 
                                        onChange={(e) => handleFilterChange('sortBy', e.target.value)}
                                    >
                                        <option value="">Default</option>
                                        <option value="price-low">Price: Low to High</option>
                                        <option value="price-high">Price: High to Low</option>
                                        <option value="distance">Distance: Nearest First</option>
                                        <option value="name-az">Name: A to Z</option>
                                        <option value="name-za">Name: Z to A</option>
                                    </select>
                                </div>
                            </div>
                        </div>
                    )}

                    {loading && (
                        <div className="loading-message">
                            <p>Loading services...</p>
                        </div>
                    )}
                    {error && (
                        <div className="error-message">
                            <p>{error}</p>
                        </div>
                    )}
                    {!loading && !error && filteredProducts.length === 0 && searchQuery && (
                        <div className="no-results-message">
                            <p>No products found for "{searchQuery}". Try a different search term.</p>
                        </div>
                    )}
                    {!loading && !error && filteredProducts.length === 0 && !searchQuery && selectedVendor && (
                        <div className="no-results-message">
                            <p>This vendor hasn't added any products yet.</p>
                        </div>
                    )}
                    {!loading && !error && filteredProducts.length > 0 && (
                        <div className="product-grid">
                            {filteredProducts.map(product => (
                                <ServiceCard
                                    key={product._id || product.id}
                                    service={product}
                                    onAddToCart={onAddToCart}
                                    onToggleWishlist={onToggleWishlist}
                                    onViewProduct={onViewProduct}
                                    isWishlisted={isProductInWishlist(product._id || product.id)}
                                    cartItems={cartItems}
                                    navigateTo={navigateTo}
                                />
                            ))}
                        </div>
                    )}
                </div>
            </section>
        </>
    );
};

export default Services1;
