import React, { useState, useEffect, useCallback } from 'react';
import HomeFurnitureCard from './HomeFurnitureCard';
import './HomeFurniture.css';

const HomeFurniture = ({ 
    wishlistItems, 
    onAddToCart, 
    onToggleWishlist, 
    onViewProduct, 
    cartItems, 
    navigateTo, 
    searchQuery,
    selectedVendor
}) => {

    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [currentSlide, setCurrentSlide] = useState(0);

    const slideImages = [
        "IMAGES/home-hero-1.jpg",
        "IMAGES/home-hero-2.jpg",
        "IMAGES/home-hero-3.jpg"
    ];

    // HERO SLIDER
    useEffect(() => {
        const slideInterval = setInterval(() => {
            setCurrentSlide(prev => (prev + 1) % slideImages.length);
        }, 5000);
        return () => clearInterval(slideInterval);
    }, [slideImages.length]);

    // DISTANCE CALCULATOR
    const getDistanceFromLatLonInKm = useCallback((lat1, lon1, lat2, lon2) => {
        const R = 6371;
        const deg2rad = deg => deg * (Math.PI / 180);
        const dLat = deg2rad(lat2 - lat1);
        const dLon = deg2rad(lon2 - lon1);
        const a =
            Math.sin(dLat / 2) ** 2 +
            Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) *
            Math.sin(dLon / 2) ** 2;
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
        return R * c;
    }, []);

    // FETCH FURNITURE PRODUCTS
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                setLoading(true);
                const response = await fetch("http://localhost:5000/api/products/category/Home Furniture");
                if (!response.ok) throw new Error("Failed to fetch furniture");

                const data = await response.json();

                // APPLY DISTANCE
                if (navigator.geolocation) {
                    navigator.geolocation.getCurrentPosition(
                        (pos) => {
                            const { latitude, longitude } = pos.coords;
                            const mapped = data.map(p => {
                                if (p.lat && p.lon) {
                                    const dist = getDistanceFromLatLonInKm(latitude, longitude, p.lat, p.lon);
                                    return { ...p, distance: `${dist.toFixed(1)} km away` };
                                }
                                return { ...p, distance: "Distance N/A" };
                            });
                            setProducts(mapped);
                            setFilteredProducts(mapped);
                        },
                        () => {
                            const mapped = data.map(p => ({ ...p, distance: "Within 5km" }));
                            setProducts(mapped);
                            setFilteredProducts(mapped);
                        }
                    );
                } else {
                    const mapped = data.map(p => ({ ...p, distance: "Within 5km" }));
                    setProducts(mapped);
                    setFilteredProducts(mapped);
                }

            } catch (err) {
                console.error(err);
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, [getDistanceFromLatLonInKm]);

    // NEW: FILTER BY VENDOR + SEARCH
    useEffect(() => {
        let filtered = [...products];

        // ✅ FILTER BY VENDOR
        if (selectedVendor) {
            filtered = filtered.filter(p => p.vendor === selectedVendor);
        }

        // ✅ FILTER BY SEARCH
        if (searchQuery && searchQuery.trim()) {
            const q = searchQuery.toLowerCase().trim();
            filtered = filtered.filter(p =>
                p.name.toLowerCase().includes(q) ||
                p.vendor.toLowerCase().includes(q) ||
                p.category.toLowerCase().includes(q)
            );
        }

        setFilteredProducts(filtered);

    }, [searchQuery, selectedVendor, products]);

    const isProductInWishlist = (productId) => {
        return wishlistItems.some(i => (i._id || i.id) === productId);
    };

    // LOADING
    if (loading) {
        return (
            <section id="homefurniture" className="section">
                <div className="container">
                    <div className="section-title"><h2>Home Furniture</h2></div>
                    <div className="loading">Loading products...</div>
                </div>
            </section>
        );
    }

    // ERROR
    if (error) {
        return (
            <section id="homefurniture" className="section">
                <div className="container">
                    <div className="section-title"><h2>Home Furniture</h2></div>
                    <div className="error">Error loading products: {error}</div>
                </div>
            </section>
        );
    }

    return (
        <>
            {/* HERO SLIDER */}
            <section className="hero" id="hero">
                <div className="hero-carousel">
                    <div
                        className="carousel-slides"
                        style={{ transform: `translateX(-${currentSlide * 100}%)` }}
                    >
                        {slideImages.map((img, i) => (
                            <div className="carousel-slide" key={i}>
                                <img src={img} alt={`Furniture slide ${i + 1}`} />
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            <section id="homefurniture" className="section">
                <div className="container">

                    {/* BACK BUTTON WHEN VENDOR SELECTED */}
                    {selectedVendor && (
                        <div style={{ marginTop: "20px", display: "flex", alignItems: "center", justifyContent: "center", position: "relative" }}>
                            <a 
                                href="#"
                                onClick={(e) => { e.preventDefault(); navigateTo("homefurniture"); }}
                                style={{ position: "absolute", left: 0, fontSize: 16, color: "#007bff" }}
                            >
                                ← Back to Vendors
                            </a>

                            <h2 style={{ margin: 0 }}>
                                {selectedVendor} – Home Furniture
                            </h2>
                        </div>
                    )}

                    {!selectedVendor && (
                        <div className="section-title">
                            <h2>Home Furniture</h2>
                        </div>
                    )}

                    {/* PRODUCT LIST */}
                    <div className="product-grid">
                        {filteredProducts.map(product => (
                            <HomeFurnitureCard
                                key={product._id}
                                product={product}
                                onAddToCart={onAddToCart}
                                onToggleWishlist={onToggleWishlist}
                                onViewProduct={onViewProduct}
                                isWishlisted={isProductInWishlist(product._id)}
                                cartItems={cartItems}
                                navigateTo={navigateTo}
                            />
                        ))}
                    </div>
                </div>
            </section>
        </>
    );
};

export default HomeFurniture;
