import React, { useState, useEffect } from 'react';
import './SellerDashboard.css';
import { categoriesData } from '../Customer/Categories/ShopByCategory/data';

const SellerDashboard = ({ seller, setAllProducts, onLogout }) => {
    const [orders, setOrders] = useState([]);
    const [products, setProducts] = useState([]);
    const [modalOpen, setModalOpen] = useState(false);
    const [modalType, setModalType] = useState('');
    const [editIndex, setEditIndex] = useState(null);
    const [formData, setFormData] = useState({});

    // Fetch seller's products and orders from backend
    useEffect(() => {
        const fetchSellerData = async () => {
            try {
                // Fetch products by seller ID
                const productsResponse = await fetch(`http://localhost:5000/api/products?sellerId=${seller.id}`);
                if (productsResponse.ok) {
                    const contentType = productsResponse.headers.get('content-type');
                    if (contentType && contentType.includes('application/json')) {
                        const sellerProducts = await productsResponse.json();
                        setProducts(sellerProducts);
                    } else {
                        throw new Error('Response is not JSON');
                    }
                } else {
                    throw new Error('Failed to fetch products');
                }

                // For now, orders are stored locally, but you can add an orders API later
                const localOrders = JSON.parse(localStorage.getItem('sellerOrders') || '[]');
                setOrders(localOrders);
            } catch (error) {
                console.error('Error fetching seller data:', error);
                // Set products to empty array if API fails
                setProducts([]);
            }
        };

        if (seller && seller.id) {
            fetchSellerData();
        }
    }, [seller]);

    const fetchProducts = async () => {
        try {
            const productsResponse = await fetch(`http://localhost:5000/api/products?sellerId=${seller.id}`);
            if (productsResponse.ok) {
                const contentType = productsResponse.headers.get('content-type');
                if (contentType && contentType.includes('application/json')) {
                    const sellerProducts = await productsResponse.json();
                    setProducts(sellerProducts);
                } else {
                    throw new Error('Response is not JSON');
                }
            } else {
                throw new Error('Failed to fetch products');
            }
        } catch (error) {
            console.error('Error fetching products:', error);
            setProducts([]);
        }
    };

    const handleAddProduct = async (e) => {
        e.preventDefault();

        // Check if all required fields are filled
        const name = formData.name?.trim();
        const price = formData.price;
        const category = formData.category?.trim();
        const stock = formData.stock;
        const imageFile = formData.imageFile;
        const description = formData.description?.trim();

        if (!name) {
            alert('Please enter a product name.');
            return;
        }
        if (!price || isNaN(price) || price <= 0) {
            alert('Please enter a valid price greater than 0.');
            return;
        }
        if (!category) {
            alert('Please select a category.');
            return;
        }
        if (stock === undefined || stock === null || stock === '' || isNaN(stock) || stock < 0) {
            alert('Please enter a valid stock quantity (0 or greater).');
            return;
        }
        if (!description) {
            alert('Please enter a product description.');
            return;
        }
        if (!imageFile) {
            alert('Please select an image file.');
            return;
        }

        try {
            const formDataToSend = new FormData();
            formDataToSend.append('name', formData.name);
            formDataToSend.append('price', parseFloat(formData.price));
            formDataToSend.append('stock', parseInt(formData.stock));
            formDataToSend.append('category', formData.category);
            formDataToSend.append('description', formData.description);
            formDataToSend.append('vendor', seller.businessName);
            formDataToSend.append('sellerId', seller.id);

            // Optional fields
            if (formData.warranty) {
                formDataToSend.append('warranty', formData.warranty);
            }
            if (formData.expiryDate) {
                formDataToSend.append('expiryDate', formData.expiryDate);
            }

            if (formData.imageFile) {
                formDataToSend.append('image', formData.imageFile);
            }

            const response = await fetch('http://localhost:5000/api/products', {
                method: 'POST',
                body: formDataToSend,
            });

            if (response.ok) {
                const contentType = response.headers.get('content-type');
                if (contentType && contentType.includes('application/json')) {
                    const newProduct = await response.json();
                    setProducts(prev => [...prev, newProduct]);
                    setAllProducts(prev => [...prev, newProduct]);
                    alert(`Product "${formData.name}" has been added successfully!`);

                    // âœ… CLEAR THE FORM AND KEEP MODAL OPEN FOR NEXT PRODUCT
                    setFormData({ category: 'Food & Dining' }); // Reset to default category
                    // Clear the file input
                    const fileInput = document.getElementById('product-image');
                    if (fileInput) {
                        fileInput.value = '';
                    }

                    fetchProducts(); // Refresh the products list
                } else {
                    throw new Error('Response is not JSON');
                }
            } else {
                alert('Failed to add product');
            }
        } catch (error) {
            console.error('Error adding product:', error);
            alert('Error adding product');
        }
    };

    const handleEditProduct = async (e) => {
        e.preventDefault();

        // Check if all required fields are filled (image is optional for edit)
        const name = formData.name?.trim();
        const price = formData.price;
        const category = formData.category?.trim();
        const stock = formData.stock;
        const description = formData.description?.trim();

        if (!name) {
            alert('Please enter a product name.');
            return;
        }
        if (!price || isNaN(price) || price <= 0) {
            alert('Please enter a valid price greater than 0.');
            return;
        }
        if (!category) {
            alert('Please select a category.');
            return;
        }
        if (stock === undefined || stock === null || stock === '' || isNaN(stock) || stock < 0) {
            alert('Please enter a valid stock quantity (0 or greater).');
            return;
        }
        if (!description) {
            alert('Please enter a product description.');
            return;
        }

        try {
            const formDataToSend = new FormData();
            formDataToSend.append('name', formData.name);
            formDataToSend.append('price', parseFloat(formData.price));
            formDataToSend.append('stock', parseInt(formData.stock));
            formDataToSend.append('category', formData.category);
            formDataToSend.append('description', formData.description);

            // Optional fields
            if (formData.warranty) {
                formDataToSend.append('warranty', formData.warranty);
            }
            if (formData.expiryDate) {
                formDataToSend.append('expiryDate', formData.expiryDate);
            }

            if (formData.imageFile) {
                formDataToSend.append('image', formData.imageFile);
            }

            const response = await fetch(`http://localhost:5000/api/products/${formData._id}`, {
                method: 'PUT',
                body: formDataToSend,
            });

            if (response.ok) {
                const contentType = response.headers.get('content-type');
                if (contentType && contentType.includes('application/json')) {
                    const updatedProduct = await response.json();
                    setProducts(prev => prev.map(p => p._id === formData._id ? updatedProduct : p));
                    setAllProducts(prev => prev.map(p => p._id === formData._id ? updatedProduct : p));
                    alert(`Product "${formData.name}" has been updated!`);
                    closeModal();
                    fetchProducts(); // Refresh the products list
                } else {
                    throw new Error('Response is not JSON');
                }
            } else {
                alert('Failed to update product');
            }
        } catch (error) {
            console.error('Error updating product:', error);
            alert('Error updating product');
        }
    };

    const handleAddOrder = (e) => {
        e.preventDefault();
        const newOrder = {
            id: formData.id || Date.now().toString(),
            customer: formData.customer,
            amount: parseFloat(formData.amount),
            status: formData.status || 'pending',
            date: formData.date || new Date().toISOString().split('T')[0]
        };

        const updatedOrders = editIndex !== null ?
            orders.map((order, i) => i === editIndex ? newOrder : order) :
            [...orders, newOrder];

        setOrders(updatedOrders);
        localStorage.setItem('sellerOrders', JSON.stringify(updatedOrders));
        closeModal();
    };

    const openModal = (type, data = null, index = null) => {
        setModalType(type);
        setEditIndex(index);
        setFormData(data || (type === 'product' ? { category: 'Food & Dining' } : {}));
        setModalOpen(true);
    };

    const closeModal = () => {
        setModalOpen(false);
        setFormData({});
        setEditIndex(null);
    };

    const deleteProduct = async (productId, index) => {
        if (window.confirm('Are you sure you want to delete this product?')) {
            try {
                const response = await fetch(`http://localhost:5000/api/products/${productId}`, {
                    method: 'DELETE',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ sellerId: seller.id })
                });
                if (response.ok) {
                    setProducts(prev => prev.filter((_, i) => i !== index));
                    setAllProducts(prev => prev.filter(p => p._id !== productId));
                    alert('Product deleted successfully');
                } else {
                    alert('Failed to delete product');
                }
            } catch (error) {
                console.error('Error deleting product:', error);
                alert('Error deleting product');
            }
        }
    };

    const deleteOrder = (index) => {
        if (window.confirm('Are you sure you want to delete this order?')) {
            const updatedOrders = orders.filter((_, i) => i !== index);
            setOrders(updatedOrders);
            localStorage.setItem('sellerOrders', JSON.stringify(updatedOrders));
        }
    };

    const totalRevenue = orders.reduce((sum, order) => sum + order.amount, 0);

    return (
        <div className="dashboard-container">
            <div className="sidebar">
                <div className="sidebar-header">
                    <h1><i className="fas fa-store"></i> Seller Center</h1>
                </div>
                <div className="seller-info">
                    <h3>{seller.name}</h3>
                    <p>Business Dashboard</p>
                </div>
                <nav className="sidebar-nav">
                    <a href="#" className="nav-item active">
                        <i className="fas fa-tachometer-alt"></i> Dashboard
                    </a>
                </nav>
            </div>

            <div className="main-content">
                <div className="header">
                    <h2>Seller Dashboard</h2>
                    <div className="user-actions">
                        <a href="/" className="back-to-store">
                            <i className="fas fa-store"></i> Visit Store
                        </a>
                        <button className="logout-btn" onClick={onLogout}>
                            <i className="fas fa-sign-out-alt"></i> Logout
                        </button>
                    </div>
                </div>

                <div className="stats-grid">
                    <div className="stat-card orders">
                        <div className="stat-icon">
                            <i className="fas fa-shopping-cart"></i>
                        </div>
                        <div className="stat-number">{orders.length}</div>
                        <div className="stat-label">New Orders</div>
                    </div>
                    <div className="stat-card revenue">
                        <div className="stat-icon">
                            <i className="fas fa-rupee-sign"></i>
                        </div>
                        <div className="stat-number">â‚¹{totalRevenue}</div>
                        <div className="stat-label">Total Revenue</div>
                    </div>
                    <div className="stat-card products">
                        <div className="stat-icon">
                            <i className="fas fa-box"></i>
                        </div>
                        <div className="stat-number">{products.length}</div>
                        <div className="stat-label">Products</div>
                    </div>
                </div>

                {/* Orders Section */}
                <div className="content-section">
                    <div className="section-header">
                        <h3>Recent Orders</h3>
                        <button className="add-btn" onClick={() => openModal('order')}>
                            <i className="fas fa-plus"></i> Add Order
                        </button>
                    </div>
                    <table className="data-table">
                        <thead>
                            <tr>
                                <th>Order ID</th>
                                <th>Customer</th>
                                <th>Amount</th>
                                <th>Status</th>
                                <th>Date</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {orders.map((order, index) => (
                                <tr key={index}>
                                    <td>{order.id}</td>
                                    <td>{order.customer}</td>
                                    <td>â‚¹{order.amount}</td>
                                    <td>
                                        <span className={`status ${order.status}`}>
                                            {order.status}
                                        </span>
                                    </td>
                                    <td>{order.date}</td>
                                    <td>
                                        <button
                                            className="edit-btn action-btn"
                                            onClick={() => openModal('order', order, index)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className="delete-btn action-btn"
                                            onClick={() => deleteOrder(index)}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                {/* Products Section */}
                <div className="content-section">
                    <div className="section-header">
                        <h3>Product Management</h3>
                        <button className="add-btn" onClick={() => openModal('product')}>
                            <i className="fas fa-plus"></i> Add Product
                        </button>
                    </div>
                    <table className="data-table">
                        <thead>
                            <tr>
                                <th>Product</th>
                                <th>Price</th>
                                <th>Stock</th>
                                <th>Category</th>
                                <th>Status</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {products.map((product, index) => (
                                <tr key={product._id}>
                                    <td>{product.name}</td>
                                    <td>â‚¹{product.price}</td>
                                    <td>{product.stock || 0}</td>
                                    <td>{product.category}</td>
                                    <td>
                                        <span className={`status ${product.status}`}>
                                            {product.status === 'pending' ? 'Pending Approval' : 
                                             product.status === 'approved' ? 'Approved' : 
                                             product.status === 'rejected' ? 'Rejected' : 'Active'}
                                        </span>
                                    </td>
                                    <td>
                                        <button
                                            className="edit-btn action-btn"
                                            onClick={() => openModal('product', product, index)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className="delete-btn action-btn"
                                            onClick={() => deleteProduct(product._id, index)}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>

            {/* Modal with Fixed CSS */}
            {modalOpen && (
                <div className="modal" style={{
                    display: 'block',
                    position: 'fixed',
                    zIndex: 1000,
                    left: 0,
                    top: 0,
                    width: '100%',
                    height: '100%',
                    overflow: 'auto',
                    backgroundColor: 'rgba(0,0,0,0.5)'
                }}>
                    <div className="modal-content" style={{
                        backgroundColor: '#fefefe',
                        margin: '2% auto',
                        padding: 0,
                        border: '1px solid #888',
                        borderRadius: '10px',
                        width: '90%',
                        maxWidth: '600px',
                        maxHeight: '90vh',
                        display: 'flex',
                        flexDirection: 'column'
                    }}>
                        {/* Modal Header - Fixed */}
                        <div style={{
                            padding: '20px 30px',
                            borderBottom: '1px solid #ddd',
                            backgroundColor: '#f8f9fa'
                        }}>
                            <h3 style={{ margin: 0 }}>
                                {editIndex !== null ? 'Edit' : 'Add'} {modalType === 'order' ? 'Order' : 'Product'}
                            </h3>
                        </div>

                        {/* Modal Body - Scrollable */}
                        <div style={{
                            padding: '20px 30px',
                            overflowY: 'auto',
                            flex: 1
                        }}>
                            <form id="productForm" onSubmit={modalType === 'order' ? handleAddOrder : (editIndex !== null ? handleEditProduct : handleAddProduct)}>
                                {modalType === 'order' ? (
                                    <>
                                        <input
                                            type="text"
                                            placeholder="Order ID"
                                            value={formData.id || ''}
                                            onChange={(e) => setFormData({ ...formData, id: e.target.value })}
                                            required
                                            style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ddd', borderRadius: '5px' }}
                                        />
                                        <input
                                            type="text"
                                            placeholder="Customer"
                                            value={formData.customer || ''}
                                            onChange={(e) => setFormData({ ...formData, customer: e.target.value })}
                                            required
                                            style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ddd', borderRadius: '5px' }}
                                        />
                                        <input
                                            type="number"
                                            placeholder="Amount"
                                            value={formData.amount || ''}
                                            onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
                                            required
                                            style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ddd', borderRadius: '5px' }}
                                        />
                                        <select
                                            value={formData.status || 'pending'}
                                            onChange={(e) => setFormData({ ...formData, status: e.target.value })}
                                            style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ddd', borderRadius: '5px' }}
                                        >
                                            <option value="pending">Pending</option>
                                            <option value="shipped">Shipped</option>
                                            <option value="completed">Completed</option>
                                        </select>
                                        <input
                                            type="date"
                                            value={formData.date || ''}
                                            onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                                            required
                                            style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ddd', borderRadius: '5px' }}
                                        />
                                    </>
                                ) : (
                                    <>
                                        <input
                                            type="text"
                                            placeholder="Product Name *"
                                            value={formData.name || ''}
                                            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                            required
                                            style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ddd', borderRadius: '5px' }}
                                        />
                                        <input
                                            type="number"
                                            placeholder="Price *"
                                            value={formData.price || ''}
                                            onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                                            required
                                            style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ddd', borderRadius: '5px' }}
                                        />
                                        <input
                                            type="number"
                                            placeholder="Stock Quantity *"
                                            value={formData.stock || ''}
                                            onChange={(e) => setFormData({ ...formData, stock: e.target.value })}
                                            min="0"
                                            required
                                            style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ddd', borderRadius: '5px' }}
                                        />
                                        <select
                                            value={formData.category || 'Food & Dining'}
                                            onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                                            required
                                            style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ddd', borderRadius: '5px' }}
                                        >
                                            {categoriesData.map((category, index) => (
                                                <option key={index} value={category.name}>
                                                    {category.name}
                                                </option>
                                            ))}
                                        </select>

                                        <textarea
                                            placeholder="Product Description * (Describe your product in detail)"
                                            value={formData.description || ''}
                                            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                                            rows="4"
                                            required
                                            style={{
                                                width: '100%',
                                                padding: '10px',
                                                marginBottom: '15px',
                                                border: '1px solid #ddd',
                                                borderRadius: '5px',
                                                fontFamily: 'inherit',
                                                fontSize: '14px',
                                                resize: 'vertical'
                                            }}
                                        />

                                        <div style={{ marginBottom: '15px' }}>
                                            <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', color: '#555', fontWeight: '500' }}>
                                                Warranty (Optional)
                                            </label>
                                            <input
                                                type="text"
                                                placeholder="e.g., 1 year, 2 years, 6 months, Lifetime"
                                                value={formData.warranty || ''}
                                                onChange={(e) => setFormData({ ...formData, warranty: e.target.value })}
                                                style={{
                                                    width: '100%',
                                                    padding: '10px',
                                                    border: '1px solid #ddd',
                                                    borderRadius: '5px'
                                                }}
                                            />
                                        </div>

                                        <div style={{ marginBottom: '15px' }}>
                                            <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', color: '#555', fontWeight: '500' }}>
                                                Expiry Date (Optional)
                                            </label>
                                            <input
                                                type="date"
                                                value={formData.expiryDate || ''}
                                                onChange={(e) => setFormData({ ...formData, expiryDate: e.target.value })}
                                                style={{
                                                    width: '100%',
                                                    padding: '10px',
                                                    border: '1px solid #ddd',
                                                    borderRadius: '5px'
                                                }}
                                            />
                                        </div>

                                        {editIndex !== null && formData.imageSrc && (
                                            <div style={{ marginBottom: '15px' }}>
                                                <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', color: '#555', fontWeight: '500' }}>
                                                    Current Image:
                                                </label>
                                                <img
                                                    src={`http://localhost:5000/${formData.imageSrc}`}
                                                    alt="Current product"
                                                    style={{ maxWidth: '150px', maxHeight: '150px', borderRadius: '5px', border: '1px solid #ddd' }}
                                                />
                                            </div>
                                        )}

                                        <div style={{ marginBottom: '15px' }}>
                                            <label htmlFor="product-image" style={{ display: 'block', marginBottom: '5px', fontSize: '14px', color: '#555', fontWeight: '500' }}>
                                                {editIndex !== null ? 'Change Product Image (optional):' : 'Product Image *:'}
                                            </label>
                                            <input
                                                type="file"
                                                id="product-image"
                                                accept="image/*"
                                                onChange={(e) => setFormData({ ...formData, imageFile: e.target.files[0] })}
                                                style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
                                            />
                                            {editIndex === null && <span style={{ color: 'red', fontSize: '12px' }}>* Required</span>}
                                        </div>
                                    </>
                                )}
                            </form>
                        </div>

                        {/* Modal Footer - Fixed at Bottom */}
                        <div style={{
                            padding: '15px 30px',
                            borderTop: '1px solid #ddd',
                            backgroundColor: '#f8f9fa',
                            display: 'flex',
                            justifyContent: 'flex-end',
                            gap: '10px'
                        }}>
                            <button 
                                type="button" 
                                onClick={closeModal}
                                style={{
                                    padding: '10px 20px',
                                    backgroundColor: '#6c757d',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '5px',
                                    cursor: 'pointer',
                                    fontSize: '14px',
                                    fontWeight: '500'
                                }}
                            >
                                Cancel
                            </button>
                            <button 
                                type="submit"
                                form="productForm"
                                style={{
                                    padding: '10px 20px',
                                    backgroundColor: '#4CAF50',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '5px',
                                    cursor: 'pointer',
                                    fontSize: '14px',
                                    fontWeight: '500'
                                }}
                            >
                                {editIndex !== null ? 'Update' : 'Save & Add Another'}
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default SellerDashboard;
