import React, { useState, useEffect, useCallback } from 'react';
import FootwearCard from './FootwearCard';
import './Footwear.css';

const Footwear = ({ 
    cartItems, 
    wishlistItems, 
    onAddToCart, 
    onToggleWishlist, 
    onViewProduct, 
    navigateTo, 
    searchQuery,
    selectedVendor
}) => {

    const [allProducts, setAllProducts] = useState({
        womens: [],
        mens: [],
        kids: []
    });

    const [filteredProducts, setFilteredProducts] = useState({
        womens: [],
        mens: [],
        kids: []
    });

    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [currentSlide, setCurrentSlide] = useState(0);

    const slideImages = [
        "../IMAGES/footweare-hero-1.jpg",
        "../IMAGES/footwere-hero-22.jpg",
        "../IMAGES/footeweare-hero-3.jpg"
    ];

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentSlide(prev => (prev + 1) % slideImages.length);
        }, 5000);
        return () => clearInterval(interval);
    }, [slideImages.length]);

    // --- Distance Calculation ---
    const getDistanceFromLatLonInKm = useCallback((lat1, lon1, lat2, lon2) => {
        const R = 6371;
        const deg2rad = deg => deg * (Math.PI / 180);
        const dLat = deg2rad(lat2 - lat1);
        const dLon = deg2rad(lon2 - lon1);
        const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
                  Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) *
                  Math.sin(dLon / 2) * Math.sin(dLon / 2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
        return R * c;
    }, []);

    const addDistanceToProducts = useCallback((products, userLat, userLon) => {
        return products.map(product => {
            if (product.lat && product.lon) {
                const distance = getDistanceFromLatLonInKm(userLat, userLon, product.lat, product.lon);
                return { ...product, distance: `${distance.toFixed(1)} km away` };
            }
            return { ...product, distance: 'Distance N/A' };
        });
    }, [getDistanceFromLatLonInKm]);

    // --- Fetch Products ---
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                setLoading(true);
                const response = await fetch("http://localhost:5000/api/products/category/Footwear");

                if (!response.ok) throw new Error("Failed to fetch products");

                const products = await response.json();

                const womens = products.filter(p => p.subcategory === "Women's Footwear");
                const mens = products.filter(p => p.subcategory === "Men's Footwear");
                const kids = products.filter(p => p.subcategory === "Kids' Footwear");

                const categorized = { womens, mens, kids };

                setAllProducts(categorized);
                setFilteredProducts(categorized);
                setError(null);
            } catch (err) {
                console.error(err);
                setError("Failed to load products");
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, []);

    // --- Geolocation Distance Setup ---
    useEffect(() => {
        if (allProducts.womens.length > 0 && navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const lat = position.coords.latitude;
                    const lon = position.coords.longitude;

                    const updated = {
                        womens: addDistanceToProducts(allProducts.womens, lat, lon),
                        mens: addDistanceToProducts(allProducts.mens, lat, lon),
                        kids: addDistanceToProducts(allProducts.kids, lat, lon),
                    };

                    setAllProducts(updated);
                    setFilteredProducts(updated);
                },
                () => {
                    const fallback = {
                        womens: allProducts.womens.map(p => ({ ...p, distance: "Within 5km" })),
                        mens: allProducts.mens.map(p => ({ ...p, distance: "Within 5km" })),
                        kids: allProducts.kids.map(p => ({ ...p, distance: "Within 5km" })),
                    };

                    setAllProducts(fallback);
                    setFilteredProducts(fallback);
                }
            );
        }
    }, [allProducts.womens.length, allProducts.mens.length, allProducts.kids.length, addDistanceToProducts]);

    // --- NEW: Vendor + Search Filtering ---
    useEffect(() => {
        let filtered = { ...allProducts };

        // ✅ FILTER BY SELECTED VENDOR
        if (selectedVendor) {
            const byVendor = (products) =>
                products.filter(p => p.vendor === selectedVendor);

            filtered = {
                womens: byVendor(allProducts.womens),
                mens: byVendor(allProducts.mens),
                kids: byVendor(allProducts.kids),
            };
        }

        // ✅ FILTER BY SEARCH QUERY
        if (searchQuery && searchQuery.trim()) {
            const q = searchQuery.toLowerCase().trim();

            const bySearch = (products) =>
                products.filter(p =>
                    p.name.toLowerCase().includes(q) ||
                    p.vendor.toLowerCase().includes(q) ||
                    p.category.toLowerCase().includes(q)
                );

            filtered = {
                womens: bySearch(filtered.womens),
                mens: bySearch(filtered.mens),
                kids: bySearch(filtered.kids),
            };
        }

        setFilteredProducts(filtered);
    }, [searchQuery, selectedVendor, allProducts]);

    const isProductInWishlist = (product) => {
        return wishlistItems.some(item =>
            (item._id || item.id) === (product._id || product.id)
        );
    };

    const renderGrid = (products) => (
        <div className="product-grid">
            {products.map(product => (
                <FootwearCard
                    key={product._id || product.id}
                    product={product}
                    onAddToCart={onAddToCart}
                    onToggleWishlist={onToggleWishlist}
                    onViewProduct={onViewProduct}
                    cartItems={cartItems}
                    navigateTo={navigateTo}
                    isWishlisted={isProductInWishlist(product)}
                />
            ))}
        </div>
    );

    if (loading) return <div className="container"><div className="loading">Loading footwear...</div></div>;
    if (error) return <div className="container"><div className="error">{error}</div></div>;

    return (
        <>
            {/* HERO SLIDER */}
            <section className="hero" id="hero">
                <div className="hero-carousel">
                    <div className="carousel-slides" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
                        {slideImages.map((img, i) => (
                            <div key={i} className="carousel-slide">
                                <img src={img} alt={`Footwear slide ${i + 1}`} />
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            {/* BACK BUTTON (LIKE MUSICAL) */}
            {selectedVendor && (
                <div className="container" style={{ marginTop: "20px", display: "flex", alignItems: "center", justifyContent: "center", position: "relative" }}>
                    <a 
                        href="#footwear"
                        onClick={(e) => { e.preventDefault(); navigateTo("footwear"); }}
                        className="back-link"
                        style={{ position: "absolute", left: "0", color: "#007bff", textDecoration: "none", fontSize: "16px" }}
                    >
                        ← Back to Vendors
                    </a>
                    <h2 style={{ margin: 0, fontSize: "2rem", color: "#333" }}>
                        {selectedVendor} - Footwear
                    </h2>
                </div>
            )}

            {/* WOMEN'S SECTION */}
            <section id="womens-footwear" className="section">
                <div className="container">
                    <div className="section-title">
                        <h2>{selectedVendor ? `${selectedVendor} - Women's Footwear` : "Women's Footwear"}</h2>
                    </div>
                    {renderGrid(filteredProducts.womens)}
                </div>
            </section>

            {/* MEN'S SECTION */}
            <section id="mens-footwear" className="section">
                <div className="container">
                    <div className="section-title">
                        <h2>{selectedVendor ? `${selectedVendor} - Men's Footwear` : "Men's Footwear"}</h2>
                    </div>
                    {renderGrid(filteredProducts.mens)}
                </div>
            </section>

            {/* KIDS' SECTION */}
            <section id="kids-footwear" className="section">
                <div className="container">
                    <div className="section-title">
                        <h2>{selectedVendor ? `${selectedVendor} - Kids' Footwear` : "Kids' Footwear"}</h2>
                    </div>
                    {renderGrid(filteredProducts.kids)}
                </div>
            </section>
        </>
    );
};

export default Footwear;
