import React, { useState, useEffect, useCallback } from 'react';
import GroceryCard from './GroceryCard';
import './Grocery.css';

const Grocery = ({
    wishlistItems = [],
    onAddToCart,
    onToggleWishlist,
    onViewProduct,
    cartItems,
    navigateTo,
    searchQuery,
    selectedVendor // ADD THIS PROP
}) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [currentSlide, setCurrentSlide] = useState(0);
    const slideImages = [
        "/IMAGES/groceries-hero-1.jpg",
        "/IMAGES/groceries-hero-2.png",
        "/IMAGES/groceries-hero-3.jpg"
    ];

    // Fetch products from API
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                setLoading(true);
                const response = await fetch('http://localhost:5000/api/products/category/Grocery');
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const data = await response.json();
                console.log('Fetched Grocery products:', data);
                console.log('Selected Vendor:', selectedVendor);

                // Add distance to products
                if (navigator.geolocation) {
                    navigator.geolocation.getCurrentPosition(
                        (position) => {
                            const userLat = position.coords.latitude;
                            const userLon = position.coords.longitude;

                            const productsWithDistance = data.map(product => {
                                if (product.lat && product.lon) {
                                    const distance = getDistanceFromLatLonInKm(userLat, userLon, product.lat, product.lon);
                                    return { ...product, distance: `${distance.toFixed(1)} km away` };
                                }
                                return { ...product, distance: 'Distance N/A' };
                            });
                            setProducts(productsWithDistance);
                        },
                        (error) => {
                            console.warn(`Geolocation error: ${error.message}`);
                            const productsWithDefaultDistance = data.map(p => ({...p, distance: 'Within 5km'}));
                            setProducts(productsWithDefaultDistance);
                        }
                    );
                } else {
                    console.warn("Geolocation is not supported by this browser.");
                    const productsWithDefaultDistance = data.map(p => ({...p, distance: 'Within 5km'}));
                    setProducts(productsWithDefaultDistance);
                }

                setError(null);
            } catch (err) {
                console.error('Error fetching products:', err);
                setError('Failed to load products. Please try again later.');
                setProducts([]);
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, [getDistanceFromLatLonInKm]);

    // Carousel auto-slide
    useEffect(() => {
        const slideInterval = setInterval(() => {
            setCurrentSlide(prev => (prev + 1) % slideImages.length);
        }, 5000);
        return () => clearInterval(slideInterval);
    }, [slideImages.length]);



    // Handle vendor and search filtering
    useEffect(() => {
        console.log('Grocery - Selected Vendor:', selectedVendor);
        console.log('Grocery - Search Query:', searchQuery);
        console.log('Grocery - Products Count:', products.length);

        let filtered = [...products];

        // CRITICAL: Filter by vendor FIRST if selectedVendor exists
        if (selectedVendor) {
            console.log('Filtering products by vendor:', selectedVendor);
            filtered = filtered.filter(product => {
                console.log(`Product: ${product.name}, Vendor: ${product.vendor}, Match: ${product.vendor === selectedVendor}`);
                return product.vendor === selectedVendor;
            });
            console.log('Products after vendor filter:', filtered.length);
        }

        // Then apply search filter
        if (searchQuery && searchQuery.trim()) {
            const query = searchQuery.toLowerCase().trim();
            filtered = filtered.filter(product => {
                const nameMatch = product.name?.toLowerCase().includes(query);
                const vendorMatch = product.vendor?.toLowerCase().includes(query);
                const categoryMatch = product.category?.toLowerCase().includes(query);
                const subcategoryMatch = product.subcategory?.toLowerCase().includes(query);
                const brandMatch = product.brand?.toLowerCase().includes(query);

                return nameMatch || vendorMatch || categoryMatch || subcategoryMatch || brandMatch;
            });
        }

        console.log('Final Filtered Grocery Products Count:', filtered.length);
        setFilteredProducts(filtered);
    }, [searchQuery, products, selectedVendor]);

    const isProductInWishlist = (productId) => {
        return wishlistItems.some(item => (item._id || item.id) === productId);
    };

    return (
        <>
            <section className="hero" id="hero">
                <div className="hero-carousel">
                    <div className="carousel-slides" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
                        {slideImages.map((img, index) => (
                            <div className="carousel-slide" key={index}>
                                <img src={img} alt={`Grocery slide ${index + 1}`} />
                            </div>
                        ))}
                    </div>
                </div>
            </section>
            <section id="groceries" className="section">
                <div className="container">
                    {selectedVendor && (
                        <div style={{ marginBottom: '20px' }}>
                            <h2 style={{ color: '#333', fontSize: '2rem' }}>{selectedVendor} - Grocery</h2>
                        </div>
                    )}
                    {searchQuery && (
                        <div className="search-results-info" style={{ textAlign: 'center', marginBottom: '20px' }}>
                            <p>
                                {filteredProducts.length} result{filteredProducts.length !== 1 ? 's' : ''} found
                                {selectedVendor ? ` in ${selectedVendor}` : ''} for "{searchQuery}"
                            </p>
                        </div>
                    )}
                    {loading ? (
                        <div style={{ textAlign: 'center', padding: '50px' }}>
                            <p>Loading grocery products...</p>
                        </div>
                    ) : error ? (
                        <div style={{ textAlign: 'center', padding: '50px', color: 'red' }}>
                            <p>Error loading products: {error}</p>
                        </div>
                    ) : filteredProducts.length === 0 ? (
                        <div className="no-results-message" style={{ textAlign: 'center', padding: '50px' }}>
                            <p>
                                No groceries found{selectedVendor ? ` in ${selectedVendor}` : ''}
                                {searchQuery ? ` for "${searchQuery}"` : ''}.
                            </p>
                        </div>
                    ) : (
                        <>
                            <div className="section-title">
                            <h2>Grocery</h2>
                            </div>
                            <div className="product-grid">
                                {filteredProducts.map(product => (
                                    <GroceryCard key={product._id || product.id} product={product} onAddToCart={onAddToCart} onToggleWishlist={onToggleWishlist} onViewProduct={onViewProduct} isWishlisted={isProductInWishlist(product._id || product.id)} cartItems={cartItems} navigateTo={navigateTo} />
                                ))}
                            </div>
                        </>
                    )}
                </div>
            </section>
        </>
    );
};

export default Grocery;
