import React from 'react';
import './ProductList.css'; // Reuse existing product list styles

const SearchResults = ({
  searchQuery,
  searchResults,
  cartItems,
  wishlistItems,
  onAddToCart,
  onToggleWishlist,
  onViewProduct,
  navigateTo
}) => {
  const isInCart = (productId) => {
    return cartItems.some(item => (item._id || item.id) === productId);
  };

  const isInWishlist = (productId) => {
    return wishlistItems.some(item => (item._id || item.id) === productId);
  };

  return (
    <div className="container">
      <div className="search-results-header">
        <h2>Search Results</h2>
        <p className="search-query">Showing results for "{searchQuery}"</p>
        <p className="results-count">{searchResults.length} product{searchResults.length !== 1 ? 's' : ''} found</p>
      </div>

      {searchResults.length > 0 ? (
        <div className="product-grid">
          {searchResults.map(product => {
            const productId = product._id || product.id;
            const inCart = isInCart(productId);
            const inWishlist = isInWishlist(productId);

            return (
              <div key={productId} className="product-card">
                <div className="product-image-container">
                  <img
                    src={product.image ? (product.image.startsWith('http') ? product.image : `http://localhost:5000/uploads/${product.image}`) : 'https://via.placeholder.com/200x150?text=No+Image'}
                    alt={product.name}
                    onError={(e) => e.target.src = 'https://via.placeholder.com/200x150?text=No+Img'}
                    onClick={() => onViewProduct(product)}
                    className="product-image"
                  />
                  <button
                    className={`wishlist-btn ${inWishlist ? 'active' : ''}`}
                    onClick={(e) => {
                      e.stopPropagation();
                      onToggleWishlist(product);
                    }}
                    title={inWishlist ? 'Remove from wishlist' : 'Add to wishlist'}
                  >
                    {inWishlist ? '‚ù§Ô∏è' : 'ü§ç'}
                  </button>
                </div>

                <div className="product-info" onClick={() => onViewProduct(product)}>
                  <h4 className="product-name">{product.name}</h4>
                  <p className="product-vendor">by {product.vendor}</p>
                  <p className="product-category">{product.category}</p>
                  <div className="product-price">‚Çπ{product.price}</div>
                  {product.stock !== undefined && (
                    <p className={`product-stock ${product.stock > 0 ? 'in-stock' : 'out-of-stock'}`}>
                      {product.stock > 0 ? `In Stock (${product.stock})` : 'Out of Stock'}
                    </p>
                  )}
                </div>

                <div className="product-actions">
                  <button
                    className={`add-to-cart-btn ${inCart ? 'added' : ''}`}
                    onClick={() => onAddToCart(product)}
                    disabled={product.stock === 0}
                  >
                    {inCart ? 'Added to Cart' : 'Add to Cart'}
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      ) : (
        <div className="no-results">
          <div className="no-results-content">
            <h3>No products found</h3>
            <p>We couldn't find any products matching "{searchQuery}".</p>
            <p>Try searching with different keywords or browse our categories.</p>
            <button
              className="browse-categories-btn"
              onClick={() => navigateTo('home')}
            >
              Browse Categories
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchResults;
