import React, { useState, useEffect, useCallback } from 'react';
import JewelleryCard from './JewelleryCard';
import './Jewellery.css';

const Jewellery = ({ 
    wishlistItems = [], 
    onAddToCart, 
    onToggleWishlist, 
    onViewProduct, 
    cartItems, 
    navigateTo, 
    searchQuery,
    selectedVendor
}) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [currentSlide, setCurrentSlide] = useState(0);
    const [filteredProducts, setFilteredProducts] = useState([]);

    // ✅ FILTER STATES
    const [showFilters, setShowFilters] = useState(false);
    const [filters, setFilters] = useState({
        priceRange: { min: '', max: '' },
        category: '',
        store: '',
        sortBy: ''
    });

    const slideImages = [
        "IMAGES/jwellery-hero-2.jpg",
        "IMAGES/jwellery-hero-2.jpg",
        "IMAGES/jwellery-hero-3.jpg"
    ];

    const getDistanceFromLatLonInKm = useCallback((lat1, lon1, lat2, lon2) => {
        const R = 6371;
        const deg2rad = (deg) => deg * (Math.PI / 180);
        const dLat = deg2rad(lat2 - lat1);
        const dLon = deg2rad(lon2 - lon1);
        const a =
            Math.sin(dLat / 2) * Math.sin(dLat / 2) +
            Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) *
            Math.sin(dLon / 2) * Math.sin(dLon / 2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
        return R * c;
    }, []);

    // ✅ COMBINED FETCH AND GEOLOCATION
    // ✅ COMBINED FETCH AND GEOLOCATION
useEffect(() => {
    const fetchProducts = async () => {
        try {
            setLoading(true);
            const response = await fetch('http://localhost:5000/api/products/category/Jewellery');
            if (!response.ok) {
                throw new Error('Failed to fetch products');
            }
            const data = await response.json();

            // ✅ SAFETY FILTER: Only keep products that actually belong to Jewellery
            const jewelleryOnly = data.filter(product => 
                product.category === 'Jewellery' || 
                product.category === 'jewellery' ||
                product.category?.toLowerCase() === 'jewellery'
            );

            console.log('Fetched products:', data.length);
            console.log('After category filter:', jewelleryOnly.length);
            console.log('Selected Vendor:', selectedVendor);

            // ✅ ADD DISTANCE IMMEDIATELY AFTER FETCH
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(
                    (position) => {
                        const userLat = position.coords.latitude;
                        const userLon = position.coords.longitude;

                        const productsWithDistance = jewelleryOnly.map(product => {
                            if (product.lat && product.lon) {
                                const distance = getDistanceFromLatLonInKm(userLat, userLon, product.lat, product.lon);
                                return { ...product, distance: `${distance.toFixed(1)} km away`, distanceValue: parseFloat(distance.toFixed(1)) };
                            }
                            return { ...product, distance: 'Distance N/A', distanceValue: 999 };
                        });
                        setProducts(productsWithDistance);
                        setFilteredProducts(productsWithDistance);
                    },
                    (error) => {
                        console.warn(`Geolocation error: ${error.message}`);
                        const productsWithDefaultDistance = jewelleryOnly.map(p => ({...p, distance: 'Within 5km', distanceValue: 5}));
                        setProducts(productsWithDefaultDistance);
                        setFilteredProducts(productsWithDefaultDistance);
                    }
                );
            } else {
                console.warn("Geolocation is not supported by this browser.");
                const productsWithDefaultDistance = jewelleryOnly.map(p => ({...p, distance: 'Within 5km', distanceValue: 5}));
                setProducts(productsWithDefaultDistance);
                setFilteredProducts(productsWithDefaultDistance);
            }

            setError(null);
        } catch (err) {
            console.error('Error fetching products:', err);
            setError('Failed to load products. Please try again later.');
            setProducts([]);
            setFilteredProducts([]);
        } finally {
            setLoading(false);
        }
    };

    fetchProducts();
}, [getDistanceFromLatLonInKm]);
    useEffect(() => {
        const slideInterval = setInterval(() => {
            setCurrentSlide(prev => (prev + 1) % slideImages.length);
        }, 5000);
        return () => clearInterval(slideInterval);
    }, [slideImages.length]);

    // ✅ APPLY FILTERS
    useEffect(() => {
        console.log('Jewellery - Selected Vendor:', selectedVendor);
        console.log('Jewellery - Search Query:', searchQuery);
        console.log('Jewellery - Products Count:', products.length);

        if (products.length === 0) {
            return;
        }

        let filtered = [...products];

        if (selectedVendor) {
            console.log('Filtering products by vendor:', selectedVendor);
            filtered = filtered.filter(product => product.vendor === selectedVendor);
            console.log('Products after vendor filter:', filtered.length);
        }

        if (searchQuery && searchQuery.trim()) {
            const query = searchQuery.toLowerCase().trim();
            filtered = filtered.filter(product => {
                const nameMatch = product.name?.toLowerCase().includes(query);
                const vendorMatch = product.vendor?.toLowerCase().includes(query);
                const categoryMatch = product.category?.toLowerCase().includes(query);
                const subcategoryMatch = product.subcategory?.toLowerCase().includes(query);
                const brandMatch = product.brand?.toLowerCase().includes(query);

                return nameMatch || vendorMatch || categoryMatch || subcategoryMatch || brandMatch;
            });
        }

        if (filters.priceRange.min) {
            filtered = filtered.filter(p => p.price >= parseFloat(filters.priceRange.min));
        }
        if (filters.priceRange.max) {
            filtered = filtered.filter(p => p.price <= parseFloat(filters.priceRange.max));
        }

        if (filters.category) {
            filtered = filtered.filter(p => p.subcategory === filters.category);
        }

        if (filters.store) {
            filtered = filtered.filter(p => 
                p.vendor?.toLowerCase().includes(filters.store.toLowerCase())
            );
        }

        if (filters.sortBy) {
            switch (filters.sortBy) {
                case 'price-low':
                    filtered.sort((a, b) => a.price - b.price);
                    break;
                case 'price-high':
                    filtered.sort((a, b) => b.price - a.price);
                    break;
                case 'distance':
                    filtered.sort((a, b) => (a.distanceValue || 999) - (b.distanceValue || 999));
                    break;
                case 'name-az':
                    filtered.sort((a, b) => a.name.localeCompare(b.name));
                    break;
                case 'name-za':
                    filtered.sort((a, b) => b.name.localeCompare(a.name));
                    break;
                default:
                    break;
            }
        }

        console.log('Final Filtered Jewellery Products Count:', filtered.length);
        setFilteredProducts(filtered);
    }, [searchQuery, products, selectedVendor, filters]);

    const handleFilterChange = (filterType, value) => {
        setFilters(prev => ({
            ...prev,
            [filterType]: value
        }));
    };

    const handlePriceRangeChange = (type, value) => {
        setFilters(prev => ({
            ...prev,
            priceRange: {
                ...prev.priceRange,
                [type]: value
            }
        }));
    };

    const clearAllFilters = () => {
        setFilters({
            priceRange: { min: '', max: '' },
            category: '',
            store: '',
            sortBy: ''
        });
    };

    const getActiveFilterCount = () => {
        let count = 0;
        if (filters.priceRange.min || filters.priceRange.max) count++;
        if (filters.category) count++;
        if (filters.store) count++;
        if (filters.sortBy) count++;
        return count;
    };

    const isProductInWishlist = (productId) => {
        return wishlistItems.some(item => (item._id || item.id) === productId);
    };

    const womensJewellery = filteredProducts.filter(p => p.subcategory === "Women's Jewellery");
    const mensJewellery = filteredProducts.filter(p => p.subcategory === "Men's Jewellery");
    const kidsJewellery = filteredProducts.filter(p => p.subcategory === "Kids' Jewellery");

    // ✅ CHECK IF ALL SUBCATEGORIES ARE EMPTY BUT FILTERED PRODUCTS EXIST
    const hasNoSubcategoryProducts = filteredProducts.length > 0 && 
        womensJewellery.length === 0 && 
        mensJewellery.length === 0 && 
        kidsJewellery.length === 0;

    return (
        <>
            <section className="hero" id="hero">
                <div className="hero-carousel">
                    <div className="carousel-slides" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
                        {slideImages.map((img, index) => (
                            <div className="carousel-slide" key={index}>
                                <img src={img} alt={`Jewellery slide ${index + 1}`} />
                            </div>
                        ))}
                    </div>
                </div>
            </section>
            <section id="jewellery" className="section">
                <div className="container">
                    {selectedVendor && (
                        <div style={{ marginBottom: '20px' }}>
                            <h2 style={{ color: '#333', fontSize: '2rem', textAlign: 'center' }}>{selectedVendor} - Jewellery</h2>
                        </div>
                    )}

                    {!selectedVendor && (
                        <div className="section-title">
                            <h2>Jewellery</h2>
                        </div>
                    )}

                    {!loading && !error && (
                        <div className="filter-section">
                            <button 
                                className="filter-toggle-btn" 
                                onClick={() => setShowFilters(!showFilters)}
                            >
                                <i className="fas fa-filter"></i>
                                Filters {getActiveFilterCount() > 0 && `(${getActiveFilterCount()})`}
                            </button>
                            {getActiveFilterCount() > 0 && (
                                <button className="clear-filters-btn" onClick={clearAllFilters}>
                                    <i className="fas fa-times"></i> Clear All
                                </button>
                            )}
                            {searchQuery && (
                                <div className="search-results-info">
                                    {filteredProducts.length} result{filteredProducts.length !== 1 ? 's' : ''} found 
                                    {selectedVendor ? ` in ${selectedVendor}` : ''} for "{searchQuery}"
                                </div>
                            )}
                        </div>
                    )}

                    {showFilters && (
                        <div className="filter-panel">
                            <div className="filter-grid">
                                <div className="filter-group">
                                    <label><i className="fas fa-rupee-sign"></i> Price Range</label>
                                    <div className="price-range-inputs">
                                        <input
                                            type="number"
                                            placeholder="Min"
                                            value={filters.priceRange.min}
                                            onChange={(e) => handlePriceRangeChange('min', e.target.value)}
                                        />
                                        <span>to</span>
                                        <input
                                            type="number"
                                            placeholder="Max"
                                            value={filters.priceRange.max}
                                            onChange={(e) => handlePriceRangeChange('max', e.target.value)}
                                        />
                                    </div>
                                </div>

                                <div className="filter-group">
                                    <label><i className="fas fa-gem"></i> Category</label>
                                    <select 
                                        value={filters.category} 
                                        onChange={(e) => handleFilterChange('category', e.target.value)}
                                    >
                                        <option value="">All Categories</option>
                                        <option value="Women's Jewellery">Women's Jewellery</option>
                                        <option value="Men's Jewellery">Men's Jewellery</option>
                                        <option value="Kids' Jewellery">Kids' Jewellery</option>
                                    </select>
                                </div>

                                <div className="filter-group">
                                    <label><i className="fas fa-store"></i> Store</label>
                                    <input
                                        type="text"
                                        placeholder="Search store"
                                        value={filters.store}
                                        onChange={(e) => handleFilterChange('store', e.target.value)}
                                    />
                                </div>

                                <div className="filter-group">
                                    <label><i className="fas fa-sort"></i> Sort By</label>
                                    <select 
                                        value={filters.sortBy} 
                                        onChange={(e) => handleFilterChange('sortBy', e.target.value)}
                                    >
                                        <option value="">Default</option>
                                        <option value="price-low">Price: Low to High</option>
                                        <option value="price-high">Price: High to Low</option>
                                        <option value="distance">Distance: Nearest First</option>
                                        <option value="name-az">Name: A to Z</option>
                                        <option value="name-za">Name: Z to A</option>
                                    </select>
                                </div>
                            </div>
                        </div>
                    )}

                    {loading ? (
                        <div style={{ textAlign: 'center', padding: '50px' }}>
                            <p>Loading jewellery products...</p>
                        </div>
                    ) : error ? (
                        <div style={{ textAlign: 'center', padding: '50px', color: 'red' }}>
                            <p>Error loading products: {error}</p>
                        </div>
                    ) : filteredProducts.length === 0 ? (
                        <div className="no-results-message" style={{ textAlign: 'center', padding: '50px' }}>
                            <p>
                                No jewellery found{selectedVendor ? ` in ${selectedVendor}` : ''}
                                {searchQuery ? ` for "${searchQuery}"` : ''}.
                            </p>
                        </div>
                    ) : hasNoSubcategoryProducts ? (
                        // ✅ FALLBACK: DISPLAY ALL PRODUCTS IN ONE GRID IF NO SUBCATEGORIES MATCH
                        <div className="product-grid">
                            {filteredProducts.map(product => (
                                <JewelleryCard 
                                    key={product._id || product.id} 
                                    product={product} 
                                    onAddToCart={onAddToCart} 
                                    onToggleWishlist={onToggleWishlist} 
                                    onViewProduct={onViewProduct} 
                                    isWishlisted={isProductInWishlist(product._id || product.id)} 
                                    cartItems={cartItems} 
                                    navigateTo={navigateTo} 
                                />
                            ))}
                        </div>
                    ) : (
                        <>
                            {womensJewellery.length > 0 && (
                                <section id="womens-jewellery" className="section">
                                    <div className="container">
                                        <div className="section-title"><h2>Women's Jewellery</h2></div>
                                        <div className="product-grid">
                                            {womensJewellery.map(product => (
                                                <JewelleryCard 
                                                    key={product._id || product.id} 
                                                    product={product} 
                                                    onAddToCart={onAddToCart} 
                                                    onToggleWishlist={onToggleWishlist} 
                                                    onViewProduct={onViewProduct} 
                                                    isWishlisted={isProductInWishlist(product._id || product.id)} 
                                                    cartItems={cartItems} 
                                                    navigateTo={navigateTo} 
                                                />
                                            ))}
                                        </div>
                                    </div>
                                </section>
                            )}

                            {mensJewellery.length > 0 && (
                                <section id="mens-jewellery" className="section">
                                    <div className="container">
                                        <div className="section-title"><h2>Men's Jewellery</h2></div>
                                        <div className="product-grid">
                                            {mensJewellery.map(product => (
                                                <JewelleryCard 
                                                    key={product._id || product.id} 
                                                    product={product} 
                                                    onAddToCart={onAddToCart} 
                                                    onToggleWishlist={onToggleWishlist} 
                                                    onViewProduct={onViewProduct} 
                                                    isWishlisted={isProductInWishlist(product._id || product.id)} 
                                                    cartItems={cartItems} 
                                                    navigateTo={navigateTo} 
                                                />
                                            ))}
                                        </div>
                                    </div>
                                </section>
                            )}

                            {kidsJewellery.length > 0 && (
                                <section id="kids-jewellery" className="section">
                                    <div className="container">
                                        <div className="section-title"><h2>Kids' Jewellery</h2></div>
                                        <div className="product-grid">
                                            {kidsJewellery.map(product => (
                                                <JewelleryCard 
                                                    key={product._id || product.id} 
                                                    product={product} 
                                                    onAddToCart={onAddToCart} 
                                                    onToggleWishlist={onToggleWishlist} 
                                                    onViewProduct={onViewProduct} 
                                                    isWishlisted={isProductInWishlist(product._id || product.id)} 
                                                    cartItems={cartItems} 
                                                    navigateTo={navigateTo} 
                                                />
                                            ))}
                                        </div>
                                    </div>
                                </section>
                            )}
                        </>
                    )}
                </div>
            </section>
        </>
    );
};

export default Jewellery;
